import React, { Component } from 'react'
import './makeroom.style.css'
import Modal from "react-responsive-modal";
import { render } from 'react-dom';
import { TAG } from './tag';
import { WithContext as ReactTags } from 'react-tag-input';
import { Link } from "react-router-dom";
import PickyDateTime from 'react-picky-date-time';

const suggestions = TAG.map((tag) => {
  return {
    id: tag,
    text: tag
  }
})

const KeyCodes = {
  comma: 188,
  enter: 13,
};

const delimiters = [KeyCodes.comma, KeyCodes.enter];

class MakeRoom extends Component {
  constructor(props) {
    super(props);

    this.state = {
      tags: [{ id: '화상회의', text: '화상회의' }],
      suggestions: suggestions,
      showPickyDateTime: true,
      date: '30',
      month: '01',
      year: '2000',
      hour: '03',
      minute: '10',
      second: '40',
      meridiem: 'PM'
    };
    this.handleDelete = this.handleDelete.bind(this);
    this.handleAddition = this.handleAddition.bind(this);
    this.handleDrag = this.handleDrag.bind(this);
    this.handleTagClick = this.handleTagClick.bind(this);
  }

  handleDelete(i) {
    const { tags } = this.state;
    this.setState({
      tags: tags.filter((tag, index) => index !== i),
    });
  }

  handleAddition(tag) {
    this.setState(state => ({ tags: [...state.tags, tag] }));
  }

  handleDrag(tag, currPos, newPos) {
    const tags = [...this.state.tags];
    const newTags = tags.slice();

    newTags.splice(currPos, 1);
    newTags.splice(newPos, 0, tag);

    // re-render
    this.setState({ tags: newTags });
  }

  handleTagClick(index) {
    console.log('The tag at index ' + index + ' was clicked');
  }

  render() {
    const { open, title, onCloseModal } = this.props;
    const { tags, suggestions,
      showPickyDateTime,
      date,
      month,
      year,
      hour,
      minute,
      second,
      meridiem } = this.state;

    return (
      <Modal open={open} onClose={onCloseModal} center >
        <div className="makeroomdiv">
          <div className="roomtitle">{title}</div>
          <div className="row-div">
            <div className="roomtitle2">방제목</div>
            <input className="roominput"></input>
          </div>

          <div className="row-div">
            <div className="roomtitle2">메인 토픽</div>
            <div className="tagdiv">
              <ReactTags
                inputFieldPosition="top"
                tags={tags}
                suggestions={suggestions}
                delimiters={delimiters}
                handleDelete={this.handleDelete}
                handleAddition={this.handleAddition}
                handleDrag={this.handleDrag}
                handleTagClick={this.handleTagClick}
              />
            </div>
          </div>

          <div className="row-div">
            <div className="roomtitle2">시작 시간</div>
            <div>
            <PickyDateTime
        size="m"// 'xs', 's', 'm', 'l'
        mode={0} //0: calendar only, 1: calendar and clock, 2: clock only; default is 0
        locale={`zh-cn`}// 'en-us' or 'zh-cn'; default is en-us
        show={showPickyDateTime} //default is false
        onClose={() => this.setState({ showPickyDateTime: false })} 
        defaultTime={`${hour}:${minute}:${second} ${meridiem}`} // OPTIONAL. format: "HH:MM:SS AM"
        defaultDate={`${month}/${date}/${year}`} // OPTIONAL. format: "MM/DD/YYYY"
        onYearPicked={res => this.onYearPicked(res)}
        onMonthPicked={res => this.onMonthPicked(res)}
        onDatePicked={res => this.onDatePicked(res)}
        onResetDate={res => this.onResetDate(res)}
        onResetDefaultDate={res => this.onResetDefaultDate(res)}
        onSecondChange={res => this.onSecondChange(res)}
        onMinuteChange={res => this.onMinuteChange(res)}
        onHourChange={res => this.onHourChange(res)}
        onMeridiemChange={res => this.onMeridiemChange(res)}
        onResetTime={res => this.onResetTime(res)}
        onResetDefaultTime={res => this.onResetDefaultTime(res)}
        onClearTime={res => this.onClearTime(res)}
      />
            </div>
          </div>

          <div className="row-div">
            <div className="roomtitle2">참여자</div>
            <input className="roominput"></input>
          </div>

          <div className="row-div2">
            <Link to="/room/test" className="linklogin">
              <button className="makebutton">개설</button>
            </Link>

            <button className="cancelbutton" onClick={onCloseModal}>취소</button>
          </div>

        </div>
      </Modal>
    )
  }
}

export default MakeRoom
